import React, { useEffect, useState } from "react";
import { Button, Col, Container, Form, Pagination, Row, Table } from "react-bootstrap";
import { PaginationControl } from "react-bootstrap-pagination-control";
import ReactPaginate from "react-paginate";

export default function Home() {

    const [ list, setList ] = useState([]);
    const [ page, setPage ] = useState(1);
    const [ perPage, setPerPage ] = useState(10);
    const [ brewedBefore, setBrewedBefore ] = useState();
    const [ beforeDate, setBeforeDate ] = useState('');
 
    const Api_Url = "https://api.punkapi.com/v2/beers";

    const paramsUrl = "?page=" + page + "&per_page=" + perPage;

    console.log("parama", paramsUrl);

    useEffect(() => {
        fetch(`${Api_Url}${paramsUrl}`)
            .then(res => res.json())
            .then(data => {
                console.log("data1", data);
                setList(data);
            });

    }, [page, perPage]);

    const handleChange = (e) =>{
        setPerPage(e.target.value);
    }

    const handleFilter = (e) => {
        setBrewedBefore(e.target.value);
    }
    console.log("date", brewedBefore);

    const filterSubmit = (e) => {
        e.preventDefault();
    }

    return (
        <div>
            <Container>
                <Row>
                    <Col className="beerTableCol">
                        <h2>Home</h2>
                        <Form>
                            <Row>
                                <Col>
                                    <Form.Control type="date" onChange={handleFilter} />
                                </Col>
                                <Col>
                                    <Button variant="primary" type="submit" onClick={filterSubmit}>Submit</Button>
                                </Col>
                            </Row>
                        </Form>
                        <Table responsive striped bordered hover variant="dark" className="mb-4 ">
                            <thead>
                                <tr>
                                    <th>Id</th>
                                    <th>name</th>
                                    <th>tagline</th>
                                    <th>first_brewed</th>
                                    <th>description</th>
                                    <th>image_url</th>
                                    <th>abv</th>
                                    <th>ibu</th>
                                    <th>target_fg</th>
                                    <th>target_og</th>
                                    <th>ebc</th>
                                    <th>srm</th>
                                    <th>ph</th>
                                    <th>attenuation_level</th>
                                    <th>volume</th>
                                    <th>boil_volume</th>
                                    <th>mash_temp</th>
                                    <th>mash_temp duration</th>
                                    <th>fermentation</th>
                                    <th>twist</th>
                                    <th>ingredients malt</th>
                                    <th>hops</th>
                                    <th>yeast</th>
                                    <th>food_pairing</th>
                                    <th>brewers_tips</th>
                                    <th>contributed_by</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    list.length > 0 ?

                                        list.map((items, index) => (
                                            <tr key={index}>
                                                <td>{items.id}</td>
                                                <td>{items.name}</td>
                                                <td>{items.tagline}</td>
                                                <td>{items.first_brewed}</td>
                                                <td>{items.description}</td>
                                                <td><img src={items.image_url} /></td>
                                                <td>{items.abv}</td>
                                                <td>{items.ibu}</td>
                                                <td>{items.target_fg}</td>
                                                <td>{items.target_og}</td>
                                                <td>{items.ebc}</td>
                                                <td>{items.srm}</td>
                                                <td>{items.ph}</td>
                                                <td>{items.attenuation_level}</td>
                                                <td>{items.volume.value} {items.volume.unit}</td>
                                                <td>{items.boil_volume.value} {items.boil_volume.unit}</td>
                                                <td>
                                                    {
                                                        items.method.mash_temp.map((data) => (
                                                            <div>{data.temp.value} {data.temp.unit}</div>
                                                        ))
                                                    }
                                                </td>
                                                <td>
                                                    {
                                                        items.method.mash_temp.map((data) => (
                                                            <div>{data.duration}</div>
                                                        ))
                                                    }
                                                </td>
                                                <td>{items.method.fermentation.temp.value} {items.method.fermentation.temp.unit}</td>
                                                <td>{items.method.twist}</td>
                                                <td>
                                                    {
                                                        items.ingredients.malt.map((data) => (
                                                            <div><strong>{data.name}</strong> - {data.amount.value} {data.amount.unit}</div>
                                                        ))
                                                    }
                                                </td>
                                                <td>
                                                    {
                                                        items.ingredients.hops.map((data) => (
                                                            <div><strong>{data.name}</strong> - {data.amount.value} {data.amount.unit} - {data.add} {data.attribute}
                                                            </div>
                                                        ))
                                                    }
                                                </td>
                                                <td>{items.ingredients.yeast}</td>
                                                <td>
                                                    <ul>
                                                        {
                                                            items.food_pairing.map((data) => (
                                                                <li>
                                                                    {data}
                                                                </li>
                                                            ))
                                                        }
                                                    </ul>
                                                </td>
                                                <td>{items.brewers_tips}</td>
                                                <td>{items.contributed_by}</td>
                                            </tr>
                                        ))

                                        :

                                        <></>
                                }
                                <tr>
                                    <td></td>
                                </tr>
                            </tbody>
                        </Table>
                    </Col>
                </Row>
                <Row>
                    <Col className="paginateCol">
                       <PaginationControl 
                            page={page}
                            between={4}
                            total={200}
                            limit={20}
                            changePage={(page) => {
                              setPage(page)
                            }}
                            ellipsis={1}
                       />
                    </Col>
                    <Col>
                        <Form>
                            <Row>
                                <Col className="listPerPageCol">
                                    <Form.Label>Per Page</Form.Label>
                                </Col>
                                <Col>
                                    <Form.Select defaultValue="10" onChange={handleChange}>
                                        <option>10</option>
                                        <option>20</option>
                                        <option>30</option>
                                    </Form.Select>
                                </Col>
                            </Row>
                        </Form>
                    </Col>
                </Row>
            </Container>
        </div>
    )
}
